// Extract payload and prepare for processing
const payload = $input.item.json.body;

// HR/recruiting keywords for Apify designation field
const hrDesignations = [
  'HR',
  'HR manager',
  'recruiter',
  'talent',
  'talent acquisition',
  'people operations',
  'people partner',
  'hiring manager',
  'executive assistant'
];

return payload.jobs.map(job => ({
  json: {
    userId: payload.userId,
    userEmail: payload.userEmail,
    contactsPerJob: payload.contactsPerJob,
    currentJob: job,
    companyUrls: [job.url],
    designation: hrDesignations,
    maxResultsPerCompany: 15
  }
}));

// Aggregate results and score contacts
const allResults = $input.all();
const jobData = $('Extract & Split Jobs').item.json;

const employees = allResults.map(item => item.json);

const hrKeywords = [
  'recruit', 'talent acquisition', 'talent partner', 'talent',
  'hr', 'human resources', 'people operations', 'people partner',
  'hiring', 'staffing', 'sourcer', 'sourcing'
];

const positionWords = jobData.currentJob.position.toLowerCase().split(' ');
const teamKeywords = positionWords.filter(w => w.length > 3);

const scored = employees.map(emp => {
  const headline = (emp.headline || emp.position || emp.title || '').toLowerCase();
  let score = 0;
  let role = 'other';

  // HR role
  const isHR = hrKeywords.some(k => headline.includes(k));
  if (isHR) {
    score = 100;
    role = 'hr';
  }

  // Team role
  const isTeam = teamKeywords.some(k => headline.includes(k));
  if (isTeam && !isHR) {
    score = 70;
    role = 'team';
  }

  // Seniority
  if (headline.includes('senior') || headline.includes('lead') || headline.includes('director') || headline.includes('head')) score += 20;
  if (headline.includes('manager')) score += 15;

  // Location
  if (emp.location && jobData.currentJob.location) {
    const empLoc = emp.location.toLowerCase();
    const jobLoc = jobData.currentJob.location.toLowerCase();
    if (empLoc.includes(jobLoc) || jobLoc.includes(empLoc)) score += 10;
  }

  return { ...emp, score, role };
});

const filtered = scored
  .filter(e => e.score >= 60)
  .sort((a, b) => b.score - a.score)
  .slice(0, jobData.contactsPerJob);

return [{
  json: {
    userId: jobData.userId,
    userEmail: jobData.userEmail,
    currentJob: jobData.currentJob,
    contactsPerJob: jobData.contactsPerJob,
    employees: filtered
  }
}];

// Create final contacts
const aiData = $input.item.json;
const aiContent = aiData.message?.content || aiData.choices?.[0]?.message?.content || '';
const jobData = $('Filter & Score').item.json;

let ranked = [];
try {
  const jsonMatch = aiContent.match(/\[.*\]/s);
  if (jsonMatch) ranked = JSON.parse(jsonMatch[0]);
} catch (e) {
  ranked = jobData.employees.map((e, i) => ({ id: i, finalScore: e.score, reasoning: e.role }));
}

const contacts = ranked.map(r => {
  const emp = jobData.employees[r.id];
  if (!emp) return null;
  return {
    name: emp.fullName || emp.name || 'Unknown',
    position: emp.headline || emp.position || emp.title || '',
    email: emp.email || null,
    company: jobData.currentJob.company,
    linkedin_url: emp.profileUrl || emp.url || emp.linkedinUrl || null,
    verified: false,
    relevance_score: r.finalScore || emp.score || 50,
    reasoning: r.reasoning || emp.role,
    source: 'linkedin_apify',
    role_type: emp.role
  };
}).filter(Boolean);

return {
  json: {
    userId: jobData.userId,
    applicationId: jobData.currentJob.applicationId,
    jobId: jobData.currentJob.jobId,
    company: jobData.currentJob.company,
    position: jobData.currentJob.position,
    contacts
  }
};

const newContacts = $('Create Contacts').item.json.contacts;
const existing = $input.all().map(i => i.json);

const existingEmails = new Set(existing.map(c => c.email?.toLowerCase()).filter(Boolean));
const existingLinkedIn = new Set(existing.map(c => c.linkedin_url?.toLowerCase()).filter(Boolean));

const unique = newContacts.filter(c => {
  if (c.email?.toLowerCase() && existingEmails.has(c.email.toLowerCase())) return false;
  if (c.linkedin_url?.toLowerCase() && existingLinkedIn.has(c.linkedin_url.toLowerCase())) return false;
  return true;
});

return {
  json: {
    userId: $('Create Contacts').item.json.userId,
    applicationId: $('Create Contacts').item.json.applicationId,
    jobId: $('Create Contacts').item.json.jobId,
    company: $('Create Contacts').item.json.company,
    position: $('Create Contacts').item.json.position,
    contacts: unique,
    totalFound: newContacts.length,
    duplicatesRemoved: newContacts.length - unique.length,
    uniqueCount: unique.length
  }
};
