// Filter to HR and recruiting contacts
const employees = $input.all();
const currentJob = $('Loop Jobs').item.json.jobs[$('Loop Jobs').itemIndex];

const hrKeywords = [
  'recruit', 'talent acquisition', 'talent partner',
  'hr', 'human resources', 'people operations', 'people partner',
  'hiring', 'staffing', 'sourcer'
];

const filtered = employees.filter(item => {
  const position = (item.json.headline || item.json.position || '').toLowerCase();
  return hrKeywords.some(keyword => position.includes(keyword));
});

// Limit to top 20 most relevant
const top20 = filtered.slice(0, 20);

if (top20.length === 0) {
  // If no HR contacts found, return empty array
  return [{
    json: {
      userId: $('Extract Payload').item.json.userId,
      userEmail: $('Extract Payload').item.json.userEmail,
      contactsPerJob: $('Extract Payload').item.json.contactsPerJob,
      currentJob: currentJob,
      employees: []
    }
  }];
}

return [{
  json: {
    userId: $('Extract Payload').item.json.userId,
    userEmail: $('Extract Payload').item.json.userEmail,
    contactsPerJob: $('Extract Payload').item.json.contactsPerJob,
    currentJob: currentJob,
    employees: top20.map(e => e.json)
  }
}];

// Parse AI response and select top N
const aiResponse = $input.item.json.message?.content || $input.item.json.choices?.[0]?.message?.content || '';
const contactsPerJob = $('Extract Payload').item.json.contactsPerJob;
const currentJob = $json.currentJob;
const employees = $json.employees || [];

let rankedContacts = [];
try {
  // Try to extract JSON from response
  const jsonMatch = aiResponse.match(/\[.*\]/s);
  if (jsonMatch) {
    rankedContacts = JSON.parse(jsonMatch[0]);
  }
} catch (e) {
  console.log('Failed to parse AI response, using fallback ranking');
  // Fallback: rank by order
  rankedContacts = employees.slice(0, contactsPerJob).map((emp, i) => ({
    id: i,
    name: emp.fullName || emp.name || 'Unknown',
    headline: emp.headline || emp.position || '',
    score: 10 - i,
    reasoning: 'Auto-ranked by order'
  }));
}

// Map back to full employee data and create final contacts
const selectedContacts = rankedContacts.slice(0, contactsPerJob).map(ranked => {
  const employee = employees[ranked.id] || employees[0];
  return {
    name: ranked.name || employee.fullName || employee.name,
    position: ranked.headline || employee.headline || employee.position,
    email: employee.email || null,
    company: currentJob.company,
    linkedin_url: employee.profileUrl || employee.url,
    verified: false,
    relevance_score: ranked.score || 5,
    reasoning: ranked.reasoning || 'Ranked by AI',
    source: 'linkedin_apify'
  };
});

return {
  json: {
    userId: $('Extract Payload').item.json.userId,
    applicationId: currentJob.applicationId,
    contacts: selectedContacts
  }
};

// Remove duplicates
const newContacts = $('Parse & Select Top N').item.json.contacts;
const existingContacts = $input.all().map(item => item.json);

const existingEmails = new Set(existingContacts.map(c => c.email?.toLowerCase()).filter(Boolean));
const existingLinkedIn = new Set(existingContacts.map(c => c.linkedin_url?.toLowerCase()).filter(Boolean));

const uniqueContacts = newContacts.filter(contact => {
  const email = contact.email?.toLowerCase();
  const linkedin = contact.linkedin_url?.toLowerCase();

  if (email && existingEmails.has(email)) return false;
  if (linkedin && existingLinkedIn.has(linkedin)) return false;

  return true;
});

return {
  json: {
    userId: $('Parse & Select Top N').item.json.userId,
    applicationId: $('Parse & Select Top N').item.json.applicationId,
    contacts: uniqueContacts,
    totalFound: newContacts.length,
    duplicatesRemoved: newContacts.length - uniqueContacts.length
  }
};
